# import pandas and matplotlib
import pandas as pd
from matplotlib import pyplot as plt


# %%
# read the file as csv
df = pd.read_csv('googleplaystore_user_reviews.csv')


# %%
# display columns
df.columns


# %%
# head/tail (5)
df.head(5)


# %%
# summary of dataset
df.describe()


# %%
# show the data types
df.dtypes


# %%
# count the no of NAs
df.isna().sum()


# %%
df.mean()


# %%
df.Sentiment_Polarity.fillna(0.182146, inplace=True)


# %%
df.Sentiment_Subjectivity.fillna(0.492704, inplace=True)


# %%
df.isna().sum()


# %%
# +ve sentiment reviews Sentiment_Polarity > 0
df_positive = df.loc[ df.Sentiment_Polarity > 0, ['Sentiment_Polarity','Sentiment_Subjectivity'] ]
df_positive.head(5)


# %%
# scatter plot of the +ve polarity vs subjectivity
df_positive.plot(kind='scatter',x='Sentiment_Subjectivity',y='Sentiment_Polarity')

